import React from 'react';
import { connect } from 'react-redux';
import { Update_name, Update_add } from '/home/ksolves/Documents/sujeet/training/ReactJs/redux-app/src/action.js';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: 'sk',
      add: 'Dl',
      showData: false
    };
  };
  render(){
    return(
      <div className="App">
      <button type="button" onClick={ () => this.setState({ showData: true })}>Click me...</button>
      {
        this.state.showData &&
        <div>
          Name:{this.props.name}<br/>
          Add: {this.props.add}
        </div>
      }
      </div>
    );
  }
}


const MapActionToProps = {Update_name, Update_add};

const MapToProps = (state) => ({
  name: state.name,
  add:state.add
});

export default connect(MapToProps, MapActionToProps)(App);
